general.custom_functions:
function vb4_replace_bbc($content)
{
	$content = preg_replace(
		array(
			'~\[(quote)=([^\]]+)\]~i',
			'~\[(.+?)="(.+?)"\]~is',
			'~\[INDENT\]~is',
			'~\[/INDENT\]~is',
			'~\[LIST=1\]~is',
		),
		array(
			'[$1="$2"]',
			'[$1=$2]',
			'	',
			'',
			'[list type=decimal]',
		), strtr($content, array('"' => '"')));

	// fixing Code tags
	$replace = array();

	preg_match('~\[code\](.+?)\[/code\]~is', $content, $matches);
	foreach ($matches as $temp)
		$replace[$temp] = htmlspecialchars($temp);
	$content = substr(strtr($content, $replace), 0, 65534);

	return $content;
}

step.presql:
TRUNCATE {$to_prefix}members;
ALTER TABLE {$to_prefix}members
	CHANGE COLUMN password_salt password_salt varchar(32) NOT NULL DEFAULT ''

TRUNCATE {$to_prefix}boards;
DELETE FROM {$to_prefix}board_permissions WHERE id_profile > 4;
ALTER TABLE {$to_prefix}boards
	CHANGE COLUMN id_board id_board SMALLINT(5) UNSIGNED NOT NULL AUTO_INCREMENT,
	CHANGE COLUMN id_cat id_cat SMALLINT(5) NOT NULL;

step.code:
$request = $db->query("
	SELECT forumid AS id_cat
	FROM {$from_prefix}forum
	WHERE parentid = '-1'");

$cats = array();
while ($row = $db->fetch_assoc($request))
	$cats[$row['id_cat']] = $row['id_cat'];
$db->free_result($request);

// Get the boards now
$request = $db->query("
	SELECT forumid AS id_board, parentid AS id_cat
	FROM {$from_prefix}forum
	WHERE parentid != '-1'");

while ($row = $db->fetch_assoc($request))
{
	foreach ($cats as $key => $value)
	{
		if ($key == $row['id_cat'])	
			$db->query("
				UPDATE {$to_prefix}boards
				SET id_cat = '$key'
				WHERE id_board = '$row[id_board]'");
	}
}
$db->free_result($request);

// id_parent is 0 when the id_cat and id_parent are equal.
$db->query("
	UPDATE {$to_prefix}boards
	SET id_parent = 0
	WHERE id_parent = id_cat");

step.preparsecode:
$keys = array('id_poll', 'id_choice', 'label', 'votes');

$options = explode('|||', $row['options']);
$votes = explode('|||', $row['votes']);

$id_poll = $row['id_poll'];
for ($i = 0, $n = count($options); $i < $n; $i++)
{
	$rows[] = array(
		'id_poll' => $id_poll,
		'id_choice' => ($i + 1),
		'label' => substr(addslashes($options[$i]), 1, 255),
		'votes' => (is_numeric($votes[$i]) ? $votes[$i] : 0),
	);
}

step.code:
$to_prefix = $this->to_prefix;

// add some importer information.
$this->db->query("
	REPLACE INTO {$to_prefix}settings (variable, value)
		VALUES ('import_time', " . time() . "),
			('enable_password_conversion', '1'),
			('imported_from', '" . $_SESSION['import_script'] . "')");
