step.preparsecode:
$request = $db->query("
	SELECT meta_value
	FROM {$from_prefix}usermeta
	WHERE user_id = $row[id_member]
	AND meta_key = 'wp_capabilities'");

list ($serialized) = $db->fetch_row($request);
$row['id_group']= array_key_exists('administrator', unserialize($serialized)) ? 1 : 0;

step.presql:
TRUNCATE {$to_prefix}topics;
TRUNCATE {$to_prefix}log_topics;
TRUNCATE {$to_prefix}log_boards;
TRUNCATE {$to_prefix}log_mark_read;
TRUNCATE {$to_prefix}polls;
TRUNCATE {$to_prefix}poll_choices;
TRUNCATE {$to_prefix}log_polls;

step.code:
$to_prefix = $this->to_prefix;

// add some importer information.
$this->db->query("
	REPLACE INTO {$to_prefix}settings (variable, value)
		VALUES ('import_time', " . time() . "),
			('enable_password_conversion', '1'),
			('imported_from', '" . $_SESSION['import_script'] . "')");
