step.preparsecode:
$pass = unserialize($row['tmp']);

if (isset($pass['hash']))
	$row['passwd'] = $pass['hash'];
else
	$row['passwd'] = sha1(md5(mktime()));

if	(isset($pass['salt']))
	$row['password_salt'] = $pass['salt'];
else
	$row['password_salt'] = '';
unset($row['tmp']);

$request = $db->query("
	SELECT 
		thread_id, last_post_id
	FROM {$from_prefix}thread
	WHERE node_id  = $row[id_board]
	ORDER BY thread_id DESC
	LIMIT 1");

	list($tmp, $row['id_last_msg']) = $db->fetch_row($request);
	$db->free_result($request);

step.code:
$to_prefix = $this->to_prefix;

// add some importer information.
$this->db->query("
	REPLACE INTO {$to_prefix}settings (variable, value)
		VALUES ('import_time', " . time() . "),
			('enable_password_conversion', '1'),
			('imported_from', '" . $_SESSION['import_script'] . "')");
